con:configuration.scriptText:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

holder.namespaces["m"] = "http://www.oorsprong.org/websamples.countryinfo"


def ISOCode = holder.getNodeValue("//m:sISOCode[contains(text(),'GN')]/following-sibling::m:sName")

log.info "Country name of that code is " + ISOCode

if(ISOCode!="Guinea") {
	log.info "failed"
} else log.info "Passed"

con:configuration.path:
declare namespace soap='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace m='http://www.oorsprong.org/websamples.countryinfo';

<MyResult>
{
for $x in //m:sName[starts-with(text(),'G')]
return  <NamesStartinG>{data($x)}</NamesStartinG>
}
</MyResult>
